Add-Type -AssemblyName System.Windows.Forms
[System.Windows.Forms.Application]::EnableVisualStyles()

$ErrorActionPreference = 'SilentlyContinue'
$wshell = New-Object -ComObject Wscript.Shell
$Button = [System.Windows.MessageBoxButton]::YesNoCancel
$ErrorIco = [System.Windows.MessageBoxImage]::Error
If (!([Security.Principal.WindowsPrincipal][Security.Principal.WindowsIdentity]::GetCurrent()).IsInRole([Security.Principal.WindowsBuiltInRole]'Administrator')) {
	Start-Process powershell.exe "-NoProfile -ExecutionPolicy Bypass -File `"$PSCommandPath`"" -Verb RunAs
	Exit
}

# GUI Specs
Write-Host "Checking winget..."

# Check if winget is installed
if (Test-Path ~\AppData\Local\Microsoft\WindowsApps\winget.exe){
    'Winget Already Installed'
}  
else{
    # Installing winget from the Microsoft Store
	Write-Host "Winget not found, installing it now."
    $ResultText.text = "`r`n" +"`r`n" + "Installing Winget... Please Wait"
	Start-Process "ms-appinstaller:?source=https://aka.ms/getwinget"
	$nid = (Get-Process AppInstaller).Id
	Wait-Process -Id $nid
	Write-Host Winget Installed
    $ResultText.text = "`r`n" +"`r`n" + "Winget Installed - Ready for Next Task"
}

$Form                            = New-Object system.Windows.Forms.Form
$Form.ClientSize                 = New-Object System.Drawing.Point(1050,1000)
$Form.text                       = "Quick Connect by Thiago Martins"
$Form.StartPosition              = "CenterScreen"
$Form.TopMost                    = $false
$Form.BackColor                  = [System.Drawing.ColorTranslator]::FromHtml("#e9e9e9")
$Form.AutoScaleDimensions        = '192, 192'
$Form.AutoScaleMode              = "Dpi"
$Form.AutoSize                   = $True
$Form.AutoScroll                 = $True
$Form.ClientSize                 = '600, 300'
$Form.FormBorderStyle            = 'FixedSingle'

# GUI Icon
$iconBase64                      = ''
$iconBytes                       = [Convert]::FromBase64String($iconBase64)
$stream                          = New-Object IO.MemoryStream($iconBytes, 0, $iconBytes.Length)
$stream.Write($iconBytes, 0, $iconBytes.Length)
$Form.Icon                       = [System.Drawing.Icon]::FromHandle((New-Object System.Drawing.Bitmap -Argument $stream).GetHIcon())
$Form.Width                      = $objImage.Width
$Form.Height                     = $objImage.Height

$id = New-Object System.Windows.Forms.label
$id.Name = "ID"
$id.Text = "ID"
$id.UseVisualStyleBackColor = $True
$id.DataBindings.DefaultDataSourceUpdateMode = 0
$id.Location = '5,10'
$id.Size = New-Object System.Drawing.Size(30,20)
$id.Font = New-Object System.Drawing.Font('Microsoft Sans Serif',14,[System.Drawing.FontStyle]([System.Drawing.FontStyle]::Bold))

$idtext = New-Object System.Windows.Forms.TextBox
$idtext.Location = New-Object System.Drawing.Point(40,7)
$idtext.Size = New-Object System.Drawing.Size(200,35)
$idtext.Font = New-Object System.Drawing.Font('Microsoft Sans Serif',14,[System.Drawing.FontStyle]([System.Drawing.FontStyle]::Bold))
$idtext.TextAlign = "Center"

$pw = New-Object System.Windows.Forms.label
$pw.Name = "Passoword"
$pw.Text = "PW"
$pw.UseVisualStyleBackColor = $True
$pw.DataBindings.DefaultDataSourceUpdateMode = 0
$pw.Location = '5,47'
$pw.Size = New-Object System.Drawing.Size(35,20)
$pw.Font = New-Object System.Drawing.Font('Microsoft Sans Serif',12,[System.Drawing.FontStyle]([System.Drawing.FontStyle]::Bold))

$pwtext = New-Object System.Windows.Forms.TextBox
$pwtext.Location = New-Object System.Drawing.Point(40,43)
$pwtext.Size = New-Object System.Drawing.Size(200,20)
$pwtext.Font = New-Object System.Drawing.Font('Microsoft Sans Serif',14,[System.Drawing.FontStyle]([System.Drawing.FontStyle]::Bold))
$pwtext.TextAlign = "Center"

$Team                            = New-Object System.Windows.Forms.Button
$Team.text                       = "TeamViewer"
$Team.width                      = 115
$Team.height                     = 35
$Team.location                   = New-Object System.Drawing.Point(245,3)
$Team.Font                       = New-Object System.Drawing.Font('Microsoft Sans Serif',12,[System.Drawing.FontStyle]([System.Drawing.FontStyle]::Bold))
$Team.BackColor                  = '#00baf7'

$Any                            = New-Object system.Windows.Forms.Button
$Any.text                       = "AnyDesk"
$Any.width                      = 115
$Any.height                     = 35
$Any.location                   = New-Object System.Drawing.Point(245,40)
$Any.Font                       = New-Object System.Drawing.Font('Microsoft Sans Serif',12,[System.Drawing.FontStyle]([System.Drawing.FontStyle]::Bold))
$Any.BackColor                  = '#f70000'

$Form.controls.AddRange(@($id,$idtext,$pw,$pwtext,$Team,$Any))

$Team.Add_Click({
    $idnum = $idtext.Text
    $pwacess = $pwtext.Text
    $TWpath = "C:\'Program Files'\TeamViewer\teamviewer.exe -i $idnum -p $pwacess"
    Invoke-Expression $TWpath
    write-host $TWpath
    })

$Any.Add_Click({
    $idnum = $idtext.Text
    $pwacess = $pwtext.Text
    $Anypath = "C:\'Program Files (x86)'\AnyDesk\anydesk.exe $idnum"
    Invoke-Expression $Anypath
    write-host $Anypath
    })

[void]$Form.ShowDialog()